SOLVER = './target/profiling/solver'
INSTANCE_GEN = './target/profiling/instance_gen'


def run_instance(file):
    t = time.monotonic_ns()
    ret = subprocess.run([
        "heaptrack",
        '-o', f"{file}.heaptrack",
        SOLVER,
        '-c', f"{file}"
    ], capture_output=True)
    duration = time.monotonic_ns() - t
    data = subprocess.run(['heaptrack_print', f"{file}.heaptrack.zst"], capture_output=True)
    mem = re.search(r'peak heap memory consumption: ([0-9\\.]+)([KMG]?)', data.stdout.decode().split('\n')[-4])
    used_memory = float(mem.groups()[0]) * (1024 if mem.groups()[1] == 'K' else 1024*1024 if mem.groups()[1] == 'M' else 1)
    res = yaml.safe_load("\n".join(ret.stdout.decode().split('\n')[3:-3]))
    return used_memory, duration, res['opened_states'], res['expanded_states']


